import React, { useEffect, useState } from 'react';
import Card from './Card';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
function Carusele({ text }) {
	const [data, setData] = useState(null);

	const getData = async () => {
		const response = await fetch('/api/v1/flights');
		const body = await response.json();
		setData({ clients: body });
	};

	useEffect(() => {
		getData();
	}, []);

	var settings = {
		dots: true,
		infinite: true,
		speed: 500,
		slidesToShow: 3,
		slidesToScroll: 1,
		responsive: [
			{
				breakpoint: 768,
				settings: {
					slidesToShow: 1,
					slidesToScroll: 1,
				},
			},
		],
	};
	return (
		<main className="carusele  m-auto p-10">
			<div
				className=""
				style={{
					fontSize: '3rem',
					fontWeight: 700,
				}}
			>
				{text}
			</div>

			<div className="m-auto h-500">
				<Slider {...settings} className="mt-10">
					<Card />
					<Card />
					<Card />
					<Card />
					<Card />
				</Slider>
			</div>
		</main>
	);
}

export default Carusele;
