import React, { useEffect, useState } from 'react';
import Select from 'react-select';
import 'rsuite/DateRangePicker/styles/index.css';
import axios from 'axios';

function Filter() {

	const getAirports = async () => {
		try {
			const response = await axios.request(options);
			console.log(response.data);
			setAirports(response.data);
		} catch (error) {
			console.error(error);
		}
	};
	useEffect(() => {
		getAirports();
	});
	const colourStyles = {
		control: (provided) => ({
			...provided,
			display: 'flex',
			flexWrap: 'nowrap',
		}),
		option: (styles, { data, isDisabled, isFocused, isSelected }) => {
			return {
				...styles,
				backgroundColor: isDisabled ? 'gray' : 'white',
				color: '#000',
				cursor: isDisabled ? 'not-allowed' : 'default',
			};
		},
	};

	const places = [
		{ value: 'Krakow [KRK]', label: 'Krakow [KRK]' },
		{ value: 'Katowice', label: 'Katowice' },
		{ value: 'Warszawa', label: 'Warszawa' },
		{ value: 'Poland', label: 'Poland' },
	];
	const dates = [
		{ value: 'April 2024', label: 'April 2024' },
		{ value: 'May 2024', label: 'May 2024' },
		{ value: 'June 2024', label: 'June 2024' },
		{ value: 'July 2024', label: 'July 2024' },
	];
	return (
		<div className="flex justify-content-center text-black">
			<div className="bg-white w-3/4  rounded-xl flex justify-content-between align-items-center p-10 gap-1">
				<div className="w-full">
					<div className="text-black">FROM</div>
					<Select
						isMulti
						className="w-full "
						options={places}
						placeholder={'City, airport or country'}
						closeMenuOnSelect={false}
						isSearchable={true}
						styles={colourStyles}
					/>
				</div>
				<div className="w-full">
					<div className="text-black">TO</div>
					<Select
						isMulti
						className="w-full"
						options={places}
						placeholder={'City, airport or country'}
						closeMenuOnSelect={false}
						isSearchable={true}
						styles={colourStyles}
					/>
				</div>

				<div className="w-full">
					<div className="text-black">WHEN</div>
					<Select
						isMulti
						className="w-full "
						options={dates}
						placeholder={'City, airport or country'}
						closeMenuOnSelect={false}
						styles={colourStyles}
					/>
				</div>
				<button class="mt-4 rounded-lg bg-[#c94f42] px-8 py-2  text-white outline-none hover:opacity-80 focus:ring">
					EXPLORE
				</button>
			</div>
		</div>
	);
}

export default Filter;
